mapper.select:
SELECT * from service_instances WHERE status='1'
AND tenant_id=#{tenantId}
ORDER BY updated_at DESC

SELECT * FROM tenants
AND CASE WHEN updated_at is null THEN created_at ELSE updated_at END > #{updatedAt}
ORDER BY updated_at DESC

SELECT
	*
FROM sales_products
WHERE
	tenant_id = #{tenantId}
	AND sales_plan_code = #{salesPlanCode}
	AND sales_plan_type_code = #{salesPlanTypeCode}
	AND start_date<=#{issueDate} AND end_date>=#{issueDate}
LIMIT 1

mapper.update:
		update 
			tenants
