		SET
			online_date=#{onlineDate}


				id IN

        		#{id}

mapper.select:
SELECT 
	data
FROM 
	service_objects
WHERE 
	tenant_id=#{tenantId}
	AND contract_no=#{contractNo}
	AND contract_branch_no=#{contractBranchNo}

mapper.update:
update 
	${tableName}
SET
	${columnName}=#{status}
WHERE 
	tenant_id=#{tenantId}
	AND contract_no=#{contractNo}

	AND contract_branch_no=#{contractBranchNo}
