mapper.select:
SELECT
	*
FROM
	tenants

	AND CASE WHEN updated_at is null THEN created_at ELSE updated_at END > #{updatedAt}
	id=#{id}
	site_url=#{domain}

ORDER BY updated_at DESC

SELECT
	*
FROM sales_products
WHERE
	tenant_id = #{tenantId}
	AND sales_plan_code = #{salesPlanCode}
	AND sales_plan_type_code = #{salesPlanTypeCode}
	AND start_date<=#{issueDate} AND end_date>=#{issueDate}
LIMIT 1

mapper.update:
		update 
			tenants
		SET
			batch_date=#{batchDate}


				id IN

        		#{id}

		update 
			tenants
		SET
			online_date=#{onlineDate}


				id IN

        		#{id}

mapper.select:
SELECT 
	data
FROM 
	service_objects
WHERE 
	tenant_id=#{tenantId}
	AND contract_no=#{contractNo}
	AND contract_branch_no=#{contractBranchNo}

mapper.update:
update 
	${tableName}
SET
	${columnName}=#{status}
WHERE 
	tenant_id=#{tenantId}
	AND contract_no=#{contractNo}

	AND contract_branch_no=#{contractBranchNo}

mapper.select:
SELECT REPLACE(JSON_EXTRACT(p.${recieverInfo}, '$.${mailType}'),'"','') as email FROM contracts c join sales_products sp ON sp.sales_plan_code=c.sales_plan_code AND sp.sales_plan_type_code=c.sales_plan_type_code AND sp.tenant_id=c.tenant_id join products p ON sp.product_code=p.product_code AND p.tenant_id=sp.tenant_id WHERE c.contract_no=#{param.contractNo} AND c.tenant_id=#{param.tenantId}
AND c.contract_branch_no=#{param.contractBranchNo}
ORDER BY c.contract_branch_no desc LIMIT 1
