INSERT INTO contract_log ( tenant_id, contract_no, contract_branch_no, sequence_no, log_type, message_code, message_group, reason_group_code, reason_code, contact_transaction_code, description, program_name, created_by ) SELECT c.tenant_id, c.contract_no, c.contract_branch_no, IFNULL(max(l.sequence_no),0)+1, #{logType}, 0, #{messageCode}, 0, #{messageGroup}, #{reasonGroupCode}, #{reasonCode}, #{contactTransactionCode}, #{description}, #{programName}, #{updatedBy} #{createdBy}
FROM (SELECT ct.* FROM contracts ct JOIN maintenance_requests mr ON ct.tenant_id=mr.tenant_id AND ct.contract_no=mr.contract_no AND ct.contract_branch_no=mr.contract_branch_no WHERE mr.tenant_id=#{tenantId} AND mr.request_no=#{requestNo}) c LEFT JOIN contract_log l ON c.tenant_id=l.tenant_id AND c.contract_no=l.contract_no AND c.contract_branch_no=l.contract_branch_no GROUP BY c.tenant_id, c.contract_no, c.contract_branch_no
FROM (SELECT tenant_id, contract_no, contract_branch_no max(contract_branch_no) as contract_branch_no FROM contracts WHERE tenant_id=#{tenantId} AND contract_no=#{contractNo} AND contract_branch_no=#{contractBranchNo}) c LEFT JOIN contract_log l ON c.tenant_id=l.tenant_id AND c.contract_no=l.contract_no AND c.contract_branch_no=l.contract_branch_no GROUP BY c.tenant_id, c.contract_no , c.contract_branch_no
