mapper.select:
SELECT ${tableInfo.key} FROM ${tableInfo.table} WHERE
tenant_id=#{tenantId} AND ${tableInfo.key}  = 
#{code}
(SELECT LPAD(${tableInfo.key}+1,#{tableInfo.size},'0') FROM ${tableInfo.table} 
WHERE tenant_id=#{tenantId} ORDER BY ${tableInfo.key} DESC LIMIT 1)
FOR UPDATE

SELECT LPAD( max(${tableInfo.key})+1,#{tableInfo.size},'0') FROM
contract_no maintenance_requests_no ${tableInfo.table}
WHERE tenant_id=#{tenantId}

mapper.update:
UPDATE

	contract_no
	maintenance_requests_no
	${tableInfo.table}

SET 
	update_count=update_count+1
	,updated_by=#{updatedBy}
WHERE tenant_id=#{tenantId} AND ${tableInfo.key}=#{code}

mapper.insert:
INSERT INTO 

	contract_no
	maintenance_requests_no
	${tableInfo.table}

(
	tenant_id,
	${tableInfo.key},
	created_by
)
VALUES(
	#{tenantId}
     ,LPAD(${code}+1,#{tableInfo.size},'0')
    ,LPAD('1',#{tableInfo.size},'0')
    ,#{updatedBy}
)
