mapper.update:
UPDATE service_objects
SET
	data=#{data}
WHERE tenant_id=#{tenantId} AND contract_no=#{contractNo} AND contract_branch_no=#{contractBranchNo}
 AND sequence_no=#{sequenceNo} AND version=#{v}

mapper.delete:
DELETE FROM service_objects
WHERE tenant_id=#{tenantId} AND contract_no=#{contractNo} AND contract_branch_no=#{contractBranchNo}
 AND sequence_no=#{sequenceNo} AND version=#{v}

DELETE FROM service_objects
WHERE tenant_id=#{tenantId} AND contract_no=#{contractNo} AND contract_branch_no=#{contractBranchNo}
 AND version=#{v}
