mapper.update:
UPDATE 
	users u
SET 
	u.update_count=u.update_count + 1,
	u.last_login_at=#{lastLoginAt}
WHERE 
	tenant_id=#{tenantId}
	AND sub=#{sub}

	SELECT 
		id
	FROM 
		users
	WHERE 
		tenant_id=#{tenantId}
		AND sub=#{sub}

mapper.select:
SELECT
	tenant_id as tenantId,
	customer_id as customerId
FROM


			customers_corporate
			WHERE 
			corp_name_kana=#{nameKanaSei}
			AND corp_addr_zip_code=#{addrZipNo}


			customers_individual
			WHERE 
			name_kana_sei = #{nameKanaSei}
			AND name_kana_mei = #{nameKanaMei}
			AND date_of_birth=DATE_FORMAT(FROM_UNIXTIME(#{dateOfBirth}/1000), '%Y-%m-%d')
			AND sex=#{sex}
			AND addr_zip_code=#{addrZipNo}


		AND tenant_id=#{tenantId}
ORDER BY updated_at DESC,customerId
